export const getCountryData = (countrySeries) => {

    if (!countrySeries) {
        return {}
    }
    const out = {
        date: null,
        confirmed: 0,
        recovered: 0,
        deaths: 0,
        suffering: 0,
        mortalityrate: 0
    }

    countrySeries.forEach((val, i, arr) => {
        out.date = val.date
        out.confirmed += parseInt(val.confirmed)
        out.recovered += parseInt(val.recovered)
        out.deaths += parseInt(val.deaths)
    })

    out.suffering = out.confirmed - (out.deaths + out.recovered)
    out.mortalityrate = out.deaths / out.confirmed

    return out;
}

export const addExtraSeriesData = (entry, countryName) => {
    const { confirmed, deaths, recovered} = entry
    return {
        ...entry,
        countryName,
        mortalityRate: deaths / confirmed,
        suffering: confirmed - (deaths + recovered)
    }
}
export const getAggregateData = (countryData) => {
    if (countryData == undefined) { return {} }
    const out = {}
    Object.keys(countryData).forEach((name) => {
        out[name] = getCountryData(countryData[name])
    })
    return out
}

export const getChartData = (countryData) => {
    const keys = Object.keys(countryData)
    console.log('keys', keys)
    console.log('countryData', countryData)
    const lastEntryIndex = keys.length - 1
    return keys.slice(0, 10).map((countryName) => countryData[countryName][lastEntryIndex])
}

export default {
    getCountryData,
    getAggregateData,
    addExtraSeriesData,
    getChartData
}
