import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import NoSSR from 'react-no-ssr';
import { Bar, BarChart, Legend, LineChart, Line, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts';
import Layout from '../../components/MyLayout'

import {fetcher} from '../index'

export default () => {
    const router = useRouter();
    const {countryName} = router.query
    const [countryDataset, setCountryDataset] = useState([])
    const { data, error } = useSWR(
        `https://pomber.github.io/covid19/timeseries.json`,
        fetcher,
        {
            onError: (err, key, config) => {
                console.log('err', err)
                console.log('key', key)
                console.log('config', config)

            },
            onSuccess: (data, key, config) => {
                const newData = []
                const dataset = data[countryName]
                newData = addExtraSeriesData(dataset, countryName)

                setDataset(newData)
            },
            initialData: {}
        }
    );
    return (
        <Layout>
            <h1>{router.query.countryName}</h1>
            <div>
                <NoSSR>
                    <ResponsiveContainer width="100%" height={400}>
                        <LineChart></LineChart>
                    </ResponsiveContainer>
                </NoSSR>
            </div>

            <table>
                <thead>
                    <tr>
                        <th>Country</th>
                        <th>Date</th>
                        <th>Confirmed</th>
                        <th>Deaths</th>
                        <th>Recovered</th>
                        <th>Suffering</th>
                        <th>Mortality Rate</th>
                    </tr>
                </thead>
                <tbody>
                    {countryDataset.map((data) => {
                        const {
                            date, confirmed, deaths, recovered, suffering, mortalityRate
                        } = data

                        return (<tr key={name}>
                            <td title="country">{name}</td>
                            <td title="date">{date}</td>
                            <td title="confirmed">{confirmed}</td>
                            <td title="deaths">{deaths}</td>
                            <td title="recovered">{recovered}</td>
                            <td title="sufferers (approx)">{suffering}</td>
                            <td title="mortality rate">{(mortalityRate * 100).toFixed(2)}%</td>
                        </tr>
                        )
                    })
                    }
                </tbody>
            </table>
        </Layout>
    );
};

