import React, {useState, useEffect} from 'react'
import NoSSR from 'react-no-ssr';
import Layout from '../components/MyLayout';
import { useRouter } from 'next/router';
import useSWR from 'swr';
import Plot from 'react-plotly.js';
import { getAggregateData, getCountryData, addExtraSeriesData } from '../utils/country'

// import Plotly from 'plotly.js-dist'


function fetcher(url) {
    return fetch(url).then(r => r.json());
}


export default function Index() {
    const [mounted, setMounted] = useState(false);
    const [dataset, setDataset] = useState({})
    const { query } = useRouter();
    const { data, error } = useSWR(
        `https://pomber.github.io/covid19/timeseries.json`,
        fetcher,
        {
            onError: (err, key, config) => {
                console.log('err', err)
                console.log('key', key)
                console.log('config', config)

            },
            onSuccess: (data, key, config) => {
                const newData = {}
                Object.keys(data).forEach((name) => {
                    const country = data[name]
                    newData[name] = country.map((entry) => addExtraSeriesData(entry))
                })
                setDataset(newData)
            },
            initialData: {}
        }
    );

    const countryNames = Object.keys(dataset)


    return (
        <Layout>
            <p>Home page</p>
            <div>
                <NoSSR>
                    <Plot
                        data={[
                            {
                                x: [1, 2, 3],
                                y: [2, 6, 3],
                                type: 'scatter',
                                mode: 'lines+markers',
                                marker: { color: 'red' },
                            },
                            { type: 'bar', x: [1, 2, 3], y: [2, 5, 3] },
                        ]}
                        layout={{ width: 320, height: 240, title: 'A Fancy Plot' }}
                    />
                </NoSSR>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Country</th>
                        <th>Date</th>
                        <th>Confirmed</th>
                        <th>Deaths</th>
                        <th>Recovered</th>
                        <th>Suffering</th>
                        <th>Mortality Rate</th>
                    </tr>
                </thead>
                <tbody>
                    {countryNames.map((name) => {
                        const countrySeries = dataset[name]
                        const lastEntryIndex = countrySeries.length - 1
                        const {
                            date, confirmed, deaths, recovered, suffering, mortalityrate
                        } = countrySeries[lastEntryIndex]

                        return (<tr key={name}>
                            <td title="country">{name}</td>
                            <td title="date">{date}</td>
                            <td title="confirmed">{confirmed}</td>
                            <td title="deaths">{deaths}</td>
                            <td title="recovered">{recovered}</td>
                            <td title="sufferers (approx)">{suffering}</td>
                            <td title="mortality rate">{(mortalityrate * 100).toFixed(2)}%</td>
                        </tr>
                        )
                    })
                    }
                </tbody>
            </table>

        </Layout>
    );
}
