import React, {useState} from 'react'
import Layout from '../components/MyLayout';
import { useRouter } from 'next/router';
import useSWR from 'swr';
import { getAggregateData, getCountryData, addExtraSeriesData } from '../utils/country'

// import Plotly from 'plotly.js-dist'


function fetcher(url) {
    return fetch(url).then(r => r.json());
}


export default function Index() {
    const [dataset, setDataset] = useState({})
    const { query } = useRouter();
    const { data, error } = useSWR(
        `https://pomber.github.io/covid19/timeseries.json`,
        fetcher,
        {
            onError: (err, key, config) => {
                console.log('err', err)
                console.log('key', key)
                console.log('config', config)

            },
            onSuccess: (data, key, config) => {
                setDataset(data)
            },
            initialData: {}
        }
    );

    const countryNames = Object.keys(data)


    return (
        <Layout>
            <p>Home page</p>
            <table>
                <thead>
                    <tr>
                        <th>Country</th>
                        <th>Date</th>
                        <th>Confirmed</th>
                        <th>Deaths</th>
                        <th>Recovered</th>
                        <th>Suffering</th>
                        <th>Mortality Rate</th>
                    </tr>
                </thead>
                <tbody>
                    {countryNames.map((name) => {
                        const countrySeries = data[name]
                        const lastEntryIndex = countrySeries.length - 1
                        const {
                            date, confirmed, deaths, recovered, suffering, mortalityrate
                        } = addExtraSeriesData(countrySeries[lastEntryIndex])

                        return (<tr key={name}>
                            <td title="country">{name}</td>
                            <td title="date">{date}</td>
                            <td title="confirmed">{confirmed}</td>
                            <td title="deaths">{deaths}</td>
                            <td title="recovered">{recovered}</td>
                            <td title="sufferers (approx)">{suffering}</td>
                            <td title="mortality rate">{(mortalityrate * 100).toFixed(2)}%</td>
                        </tr>
                        )
                    })
                    }

                </tbody>
            </table>

        </Layout>
    );
}
