import Layout from '../components/MyLayout';
import { useRouter } from 'next/router';
import useSWR from 'swr';

// import Plotly from 'plotly.js-dist'


function fetcher(url) {
    return fetch(url).then(r => r.json());
}


export default function Index() {
    const { query } = useRouter();
    const { data, error } = useSWR(
        `https://pomber.github.io/covid19/timeseries.json`,
        fetcher,
        {
            onError: (err, key, config) => {
                console.log('err', err)
                console.log('key', key)
                console.log('config', config)

            },
            initialData: {}
        }
    );
    console.log('error', data)
    console.log('data', data)

    const countryNames = Object.keys(data)
    const getCountryData = (countrySeries) => {

        if (!countrySeries) {
            return {}
        }
        const out = {
            date: null, 
            confirmed: 0,
            recovered: 0,
            deaths: 0,
            suffering: 0,
            mortalityrate: 0
        }

        countrySeries.forEach((val, i, arr) =>{
            out.date = val.date
            out.confirmed += parseInt(val.confirmed)
            out.recovered += parseInt(val.recovered)
            out.deaths += parseInt(val.deaths)
        })

        out.suffering = out.confirmed - (out.deaths + out.recovered)
        out.mortalityrate = out.deaths / out.confirmed

        return out;
    }

    const getAggregateData = () => {
        if (data == undefined) { return {} }
        const out = {}
        Object.keys(data).forEach((name) => {
            out[name] = getCountryData(data[name])
        })
        return out
    }

    const aggregateData = getAggregateData()
    console.log('aggregateData', aggregateData)
    // const map = data ? ()
    return (
        <Layout>
            <p>Home page</p>
            <table>
                <thead>
                    <th>Country</th>
                    <th>Date</th>
                    <th>Confirmed</th>
                    <th>Deaths</th>
                    <th>Recovered</th>
                    <th>Suffering</th>
                    <th>Mortality Rate</th>
                </thead>
                <tbody>
                    {countryNames.map((name) => {
                        const country = getCountryData(data[name])
                        return (<tr>
                            <td title="country">{name}</td>
                            <td title="date">{country.date}</td>
                            <td title="confirmed">{country.confirmed}</td>
                            <td title="deaths">{country.deaths}</td>
                            <td title="recovered">{country.recovered}</td>
                            <td title="sufferers (approx)">{country.suffering}</td>
                            <td title="mortality rate">{(country.mortalityrate * 100).toFixed(2)}%</td>
                        </tr>
                    )
                })
            }

                </tbody>
            </table>

        </Layout>
    );
}
