import React, {useState, useEffect} from 'react'
import NoSSR from 'react-no-ssr';
import Layout from '../components/MyLayout';
import { useRouter } from 'next/router';
import useSWR from 'swr';
// import Plot from 'react-plotly.js';
import { BarChart, Legend,  LineChart, Line, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts';

import { getAggregateData, getCountryData, addExtraSeriesData } from '../utils/country'

// import Plotly from 'plotly.js-dist'


function fetcher(url) {
    return fetch(url).then(r => r.json());
}


export default function Index() {
    const [mounted, setMounted] = useState(false);
    const [dataset, setDataset] = useState({})
    const { query } = useRouter();
    const { data, error } = useSWR(
        `https://pomber.github.io/covid19/timeseries.json`,
        fetcher,
        {
            onError: (err, key, config) => {
                console.log('err', err)
                console.log('key', key)
                console.log('config', config)

            },
            onSuccess: (data, key, config) => {
                const newData = {}
                Object.keys(data).forEach((name) => {
                    const country = data[name]
                    newData[name] = country.map((entry) => addExtraSeriesData(entry, name))
                })
                setDataset(newData)
            },
            initialData: {}
        }
    );

    const countryNames = Object.keys(dataset)
    const chartData = Object.keys(dataset).map((countryName) => dataset[countryName])

    return (
        <Layout>
            <p>Home page</p>
            <div>
                <NoSSR>
                    <ResponsiveContainer width="100%" height={200}>
                        {/* <LineChart data={chartData}>
                            <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                        </LineChart> */}
                        <BarChart data={chartData}>
                            <XAxis dataKey="countryName" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="confirmed" fill="#8884d8" />
                            <Bar dataKey="deaths" fill="#82ca9d" />
                        </BarChart>
                    </ResponsiveContainer>

                </NoSSR>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Country</th>
                        <th>Date</th>
                        <th>Confirmed</th>
                        <th>Deaths</th>
                        <th>Recovered</th>
                        <th>Suffering</th>
                        <th>Mortality Rate</th>
                    </tr>
                </thead>
                <tbody>
                    {countryNames.map((name) => {
                        const countrySeries = dataset[name]
                        const lastEntryIndex = countrySeries.length - 1
                        const {
                            date, confirmed, deaths, recovered, suffering, mortalityRate
                        } = countrySeries[lastEntryIndex]

                        return (<tr key={name}>
                            <td title="country">{name}</td>
                            <td title="date">{date}</td>
                            <td title="confirmed">{confirmed}</td>
                            <td title="deaths">{deaths}</td>
                            <td title="recovered">{recovered}</td>
                            <td title="sufferers (approx)">{suffering}</td>
                            <td title="mortality rate">{(mortalityRate * 100).toFixed(2)}%</td>
                        </tr>
                        )
                    })
                    }
                </tbody>
            </table>

        </Layout>
    );
}
